import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props) {
    super(props)
      this.state = {
        squares: [],
        torpIndexes: [],
        shipIndexes: []
      }
  }

  componentDidMount() {
    let squares = []
    for (let i=0; i<100; i++) squares.push("")
    this.setState({
      squares: squares
    })
  }

  handleClick = (value, index) => {
    let { squares } = this.state
    let { torpIndexes } = this.set
    torpIndexes.push(index)
    squares[index] = "X"
    this.setState({
      squares: squares,
      torpIndexes: torpIndexes
    })
  }

  render(){
    let squares = this.state.squares.map((value, index) => {
      return(
        <Square
          value = { value }
          index = { index }
          key = { index }
          squares = { this.state.squares }
          handleClick = { this.handleClick }
        />
      )
    })
    return(
      <>
        <div id="body">
          <h1>Battleship App</h1>
          <div id="board">
            { squares }
          </div>
        </div>
      </>
    )
  }
}
export default App
