import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props) {
    super(props)
      this.state = {
        squares: []
      }
  }

  componentDidMount() {
    let squares = []
    for (let i=0; i<101; i++) squares.push("")
    this.setState({
      squares: squares
    })
  }

  render(){
    let squares = this.state.squares.map((value, index) => {
      return(
        <Square
          value = { value }
          index = { index }
          key = { index }
        />
      )
    })
    return(
      <>
        <div id="body">
        <h1>Battleship App</h1>
        { squares }
        </div>
      </>
    )
  }
}
export default App
