import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props) {
    super(props)
      this.state = {
        squares: [],
        torpIndexes: [],
        shipIndexes: [],
        turns: 0,
        hitCounter: 5,
        sunkShip: false,
        won: false,
        missed: false
      }
  }

  componentDidMount() {
    let squares = []
    for (let i=0; i<100; i++) squares.push("🌊")
    let shipIndexes = []
    while(shipIndexes.length < 5){
      let r = Math.floor(Math.random() * 100)
      if(shipIndexes.indexOf(r) === -1) shipIndexes.push(r)
    }
    shipIndexes.map(index => {
      return squares[index] = "🚢"
    })
    this.setState({
      squares: squares,
      shipIndexes: shipIndexes
    })
    console.log(shipIndexes)
  }

  handleClick = (value, index) => {
    let { squares } = this.state
    let { torpIndexes } = this.state
    let { turns } = this.state
    let { hitCounter } = this.state
    let { shipIndexes } = this.state
    let { sunkShip } = this.state
    let { missed } = this.state
    let { won } = this.state
    if (shipIndexes.includes(index) && !torpIndexes.includes(index) && !won) {
      squares[index] = "☠️"
      torpIndexes.push(index)
      turns++
      hitCounter--
      sunkShip = true
      missed = false
    }
    else if (!torpIndexes.includes(index) && !won) {
      squares[index] = "💨"
      torpIndexes.push(index)
      turns++
      sunkShip = false
      missed = true
    }
    this.setState({ squares, torpIndexes, turns, hitCounter, sunkShip, missed })
    if (hitCounter === 0) {
      won = true
    }
    this.setState({ won })
  }

  reset = () => {
    this.setState({
      squares: [],
      torpIndexes: [],
      shipIndexes: [],
      turns: 0,
      hitCounter: 5,
      sunkShip: false,
      won: false,
      missed: false
    })
    this.componentDidMount()
  }

  render(){
    let squares = this.state.squares.map((value, index) => {
      return(
        <Square
          value = { value }
          index = { index }
          key = { index }
          squares = { this.state.squares }
          handleClick = { this.handleClick }
        />
      )
    })


    return(
      <>
        <div id="body">
          <h1>Battleship App</h1>
          <div id="message">
          {this.state.won && "You destroyed all the battleships!"}
          {this.state.sunkShip && "You hit a battleship!"}
          {this.state.missed && "You missed!"}
          </div>
          <div id="board">
            { squares }
          </div>
          <div> Turns: { this.state.turns } <br/> Battleships left: { this.state.hitCounter }</div>
          <button id="reset" onClick = { this.reset }>Reset</button>
        </div>
      </>
    )
  }
}
export default App
