import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props) {
    super(props)
      this.state = {
        squares: [],
        hitSquareIndexes: []
      }
  }

  componentDidMount() {
    let squares = []
    for (let i=0; i<100; i++) squares.push("")
    this.setState({
      squares: squares
    })
  }

  handleClick = (value, index) => {
    let { hitSquareIndexes } = this.set
    hitSquareIndexes.push(index)
    this.setState({
      hitSquareIndexes: hitSquareIndexes
    })
  }

  render(){
    let squares = this.state.squares.map((value, index) => {
      return(
        <Square
          value = { value }
          index = { index }
          key = { index }
          squares = { this.state.squares }
          handleClick = { this.handleClick }
        />
      )
    })
    return(
      <>
        <div id="body">
          <h1>Battleship App</h1>
          <div id="board">
            { squares }
          </div>
        </div>
      </>
    )
  }
}
export default App
