import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props) {
    super(props)
      this.state = {
        squares: [],
        torpIndexes: [],
        shipIndexes: [],
        torpCounter: 25,
        hitCounter: 5,
        sunkShip: false,
        done: false,
        missed: false
      }
  }

  componentDidMount() {
    let squares = []
    for (let i=0; i<100; i++) squares.push("🌊")
    let shipIndexes = []
    while(shipIndexes.length < 5){
      let r = Math.floor(Math.random() * 100)
      if(shipIndexes.indexOf(r) === -1) shipIndexes.push(r)
    }
    shipIndexes.map(index => {
      return squares[index] = "🚢"
    })
    this.setState({
      squares: squares,
      shipIndexes: shipIndexes
    })
    console.log(shipIndexes)
  }

  handleClick = (value, index) => {
    let { squares } = this.state
    let { torpIndexes } = this.state
    let { torpCounter } = this.state
    let { hitCounter } = this.state
    let { shipIndexes } = this.state
    let { sunkShip } = this.state
    let { missed } = this.state
    let { done } = this.state
    if (shipIndexes.includes(index) && !torpIndexes.includes(index) && !done) {
      squares[index] = "☠️"
      torpIndexes.push(index)
      hitCounter--
      torpCounter--
      hitCounter > 0 ? sunkShip = true : sunkShip = false
      missed = false
    }
    else if (!torpIndexes.includes(index) && !done) {
      squares[index] = "💨"
      torpIndexes.push(index)
      torpCounter--
      sunkShip = false
      missed = true
    }
    this.setState({ squares, torpIndexes, torpCounter, hitCounter, sunkShip, missed })
    if (hitCounter === 0 || torpCounter === 25 ) done = true
    this.setState({ done })
  }

  reset = () => {
    this.setState({
      squares: [],
      torpIndexes: [],
      shipIndexes: [],
      torpCounter: 25,
      hitCounter: 5,
      sunkShip: false,
      done: false,
      missed: false
    })
    this.componentDidMount()
  }

  render(){
    let { torpCounter } = this.state
    let { hitCounter } = this.state
    let squares = this.state.squares.map((value, index) => {
      return(
        <Square
          value = { value }
          index = { index }
          key = { index }
          squares = { this.state.squares }
          handleClick = { this.handleClick }
        />
      )
    })
    let winLoseMessage
    if (hitCounter === 0) {
      winLoseMessage = <h3>"You destroyed all the battleships!"</h3>
    } else if (torpCounter === 0 && hitCounter !== 0) {
      winLoseMessage = <h3>"No more torpedoes! You lose!"</h3>
    }
    return(
      <>
        <div id="body">
          <h1>Battleship App</h1>
          <div id="message">
          { winLoseMessage }
          {this.state.sunkShip && <h3>"You hit a battleship!"</h3>}
          {this.state.missed && <h3>"You missed!"</h3>}
          </div>
          <div id="board">
            { squares }
          </div>
          <div> Misses: { this.state.torpCounter } <br/> Battleships left: { this.state.hitCounter }</div>
          <button id="reset" onClick = { this.reset }>Reset</button>
        </div>
      </>
    )
  }
}
export default App
