import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props) {
    super(props)
      this.state = {
        squares: [],
        torpIndexes: [],
        shipIndexes: [],
        turns: 0,
        hitCounter: 0,
        messages: {
          sunkShip: false,
          missed: false
        }
      }
  }

  componentDidMount() {
    let squares = []
    for (let i=0; i<100; i++) squares.push("🌊")
    let shipIndexes = []
    while(shipIndexes.length < 5){
      let r = Math.floor(Math.random() * 100)
      if(shipIndexes.indexOf(r) === -1) shipIndexes.push(r)
    }
    shipIndexes.map(index => {
      return squares[index] = "🚢"
    })
    this.setState({
      squares: squares,
      shipIndexes: shipIndexes
    })
    console.log(shipIndexes)
  }

  handleClick = (value, index) => {
    let { squares } = this.state
    let { torpIndexes } = this.state
    let { turns } = this.state
    let { hitCounter } = this.state
    let { shipIndexes } = this.state
    let { messages } = this.state
    if (shipIndexes.includes(index) && !torpIndexes.includes(index)) {
      squares[index] = "☠️"
      torpIndexes.push(index)
      turns++
      hitCounter++
      messages.sunkShip = true
    }
    else if (!torpIndexes.includes(index)) {
      squares[index] = "💨"
      torpIndexes.push(index)
      turns++
      messages.sunkShip = false
    }
    this.setState({
      squares: squares,
      torpIndexes: torpIndexes,
      turns: turns,
      hitCounter: hitCounter,
      messages: messages
    })
  }

  reset = () => {
    this.setState({
      squares: [],
      torpIndexes: [],
      shipIndexes: [],
      turns: 0,
      hitCounter: 0
    })
    this.componentDidMount()
  }

  render(){
    let squares = this.state.squares.map((value, index) => {
      return(
        <Square
          value = { value }
          index = { index }
          key = { index }
          squares = { this.state.squares }
          handleClick = { this.handleClick }
        />
      )
    })


    return(
      <>
        <div id="body">
          <h1>Battleship App</h1>
          <div id="message">
          {this.state.sunkShip && "You hit a battleship!"}
          </div>
          <div id="board">
            { squares }
          </div>
          <div> { this.state.turns } <br/> { this.state.hitCounter }</div>
          <button id="reset" onClick = { this.reset }>Reset</button>
        </div>
      </>
    )
  }
}
export default App
