import React, { Component } from 'react'
import '../App.css'

const Messages = () => {
  let message
  if (props.hitCounter === 0) {
    message = <h3>You destroyed all the battleships!</h3>
  } else if (props.torpCounter === 0 && props.hitCounter !== 0) {
    message = <h3>No more torpedoes! You lose!</h3>
  } else if (props.sunkShip) {
    message = <h3>You hit a battleship!</h3>
  } else if (props.missed) {
    message = <h3>You missed!</h3>
  }
  return(
    <>
      { message }
    </>
    )
}
export default Messages
