import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props) {
    super(props)
      this.state = {
        squares: [],
        torpIndexes: [],
        shipIndexes: [],
        turns: 0
      }
  }

  componentDidMount() {
    let squares = []
    for (let i=0; i<100; i++) squares.push("")
    let shipIndexes = []
    while(shipIndexes.length < 5){
      let r = Math.floor(Math.random() * 100)
      if(shipIndexes.indexOf(r) === -1) shipIndexes.push(r)
    }
    this.setState({
      squares: squares,
      shipIndexes: shipIndexes
    })
  }

  handleClick = (value, index) => {
    let { squares } = this.state
    let { torpIndexes } = this.state
    let { turns } = this.state
    if (!torpIndexes.includes(index)) {
      torpIndexes.push(index)
      turns++
    }
    squares[index] = "X"
    this.setState({
      squares: squares,
      torpIndexes: torpIndexes,
      turns: turns
    })
  }

  render(){
    let squares = this.state.squares.map((value, index) => {
      return(
        <Square
          value = { value }
          index = { index }
          key = { index }
          squares = { this.state.squares }
          handleClick = { this.handleClick }
        />
      )
    })
    return(
      <>
        <div id="body">
          <h1>Battleship App</h1>
          <div id="board">
            { squares }
          </div>
          <div> { this.state.turns } </div>
        </div>
      </>
    )
  }
}
export default App
